select from shetuanxinxi where 1=1 and yonghuming like concat('%',#{yonghuming,jdbcType=VARCHAR},'%') and xingming like concat('%',#{xingming,jdbcType=VARCHAR},'%') and shetuanmingcheng like concat('%',#{shetuanmingcheng,jdbcType=VARCHAR},'%') and shetuanleixing like concat('%',#{shetuanleixing,jdbcType=VARCHAR},'%') and tupian like concat('%',#{tupian,jdbcType=VARCHAR},'%') and dizhi like concat('%',#{dizhi,jdbcType=VARCHAR},'%') and gaiyao like concat('%',#{gaiyao,jdbcType=VARCHAR},'%') and jianjie like concat('%',#{jianjie,jdbcType=VARCHAR},'%') and issh like concat('%',#{issh,jdbcType=VARCHAR},'%')
order by id desc
limit #{pageno},#{pageSize}
